import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {

  businesses = [
    {id:1, name: 'Pizza Place', category: 'Restaurant', description: 'Best pizza in town',image:'data:image/jpeg;base64,' },
    {id:2, name: 'Book Store', category: 'Shop', description: 'Wide range of books',image:'data:image/jpeg;base64,' },
    {id:3, name: 'Gym Center', category: 'Fitness', description: 'State-of-the-art equipment',image:'data:image/jpeg;base64,' },
    {id:4, name: 'Pizza Place', category: 'Restaurant', description: 'Best pizza in town',image:'data:image/jpeg;base64,' },
    {id:5, name: 'Book Store', category: 'Shop', description: 'Wide range of books',image:'data:image/jpeg;base64,' },
    {id:6, name: 'Gym Center', category: 'Fitness', description: 'State-of-the-art equipment',image:'data:image/jpeg;base64,' },
    {id:7, name: 'Pizza Place', category: 'Restaurant', description: 'Best pizza in town',image:'data:image/jpeg;base64,' },
    {id:8 ,name: 'Book Store', category: 'Shop', description: 'Wide range of books',image:'data:image/jpeg;base64,' },
    {id:9, name: 'Gym Center', category: 'Fitness', description: 'State-of-the-art equipment',image:'data:image/jpeg;base64,' }
  ];

  constructor(private router:Router){

  }

  onSearch(event: any) {
    const query = event.target.value.toLowerCase();
    this.businesses = this.businesses.filter(
      business => business.name.toLowerCase().includes(query) || business.category.toLowerCase().includes(query)
    );
  }

  viewDetails(business: any) {
    console.log('Viewing details for:', business);
    this.router.navigate(['/business/',business.id])
  }

}
